function getCookie(name) {
    let v = document.cookie.match('(^|;) ?' + name + '=([^;]*)(;|$)');
    return v ? v[2] : null;
}

let vetCookiePopup = getCookie('CEVA_VET_POPUP');

if (vetCookiePopup === null) {
    document.getElementById("smart-popin").style.display = "block";
}

function closeVetModal() {
    let expires;
    let date = new Date();
    date.setTime(date.getTime() + (13 * 30 * 24 * 3600));
    expires = "; expires=" + date.toGMTString();
    document.cookie = "CEVA_VET_POPUP=true" + expires + '; path=/';
    document.getElementById("smart-popin").style.display = "none";
}
